mapper.insert:

  SELECT COALESCE(MAX(LOG_NO), 0)+1 AS LOG_NO FROM TB_LOG

INSERT INTO TB_LOG (
  LOG_NO
  ,LOG_TYPE
  ,LOG_IP
  ,LOG_INFO
  ,LOG_DATE
  ,LOG_TIME
  ,LOG_USER_NO
  ,LOG_DROP_YN
  ,LOG_URI
) VALUES (
  #{logVo.logNo}
  ,#{logVo.logType}
  ,#{logVo.logIp}
  ,#{logVo.logInfo}
  ,CURRENT_DATE
  ,CURRENT_TIME
  ,#{logVo.logUserNo}
  ,'0'
  ,#{logVo.logUri}
)

mapper.select:
SELECT *
FROM TB_LOG 
WHERE 1=1

  AND LOG_TYPE = #{LOG_TYPE}


  AND LOG_IP = #{LOG_IP}


  AND LOG_USER_NO = #{LOG_USER_NO}


  AND LOG_URI = #{LOG_URI}


  AND LOG_DATE = CURRENT_DATE
  AND LOG_TIME BETWEEN CURRENT_TIME - INTERVAL '${TIME}HOUR' AND CURRENT_TIME
